import React , {useState} from "react";
import "./ExpenseForm.css";



const ExpenseForm = () => {

    const [enteredTitle , setEnteredTitle] = useState('');
    const [enteredAmount , setEnteredAmount] = useState(0);
    const [enteredDate , setEnteredDate] = useState('2019-01-01');

    const titleChangeHandler = (event) => {
        
        setEnteredTitle(event.target.value); 
    }
    
    const amountChangeHandler = (event) => {
        setEnteredAmount(event.target.value);
    }

    const dateChangeHandler = (event) => {
        setEnteredDate(event,target.date);
    }

    return (
        <form>
            <div className="new-expense__controls" >
                <div className="new-expense__control" >
                    <label>Title</label>
                    <input onChange={titleChangeHandler} type='text' />
                </div>
                <div className="new-expense__control" >
                    <label>Amount (in Rupees) </label>
                    <input onChange={amountChangeHandler} type='number' min="1" step="0.01" />
                </div>
                <div className="new-expense__control" >
                    <label>Date</label>
                    <input onChange={dateChangeHandler} type='date' min="2019-01-01" max="2023-12-31"  />
                </div>
            </div>
            <div className="new-expense__actions">
                <button type="submit" >Add Expense</button>
            </div>
        </form>
    )
}

export default ExpenseForm;
